import React from "react";

function DisplayWeather(props) {
  const message = "Not Found";
  const { data } = props;
  const iconurl =
    "http://openweathermap.org/img/wn/" +
    `${data.cod !== 404 ? data.weather[0].icon : null}` +
    ".png";
  return (
    <div className="displayWeather">
      {data.cod !== 404 ? (
        <React.Fragment>
          <div className="main-card">
            <p className="card-title">
              {data.name}, {data.sys.country}
            </p>
            <p>Latitude: {data.coord.lat}<sup>o</sup> {" "} Longitude: {data.coord.lon}<sup>o</sup></p>
            <h1>
              Feels Like: {Math.floor(data.main.temp - 273.15)}
              <sup>o</sup>C
            </h1>
            <p>
              {Math.floor(data.main.temp - 273.15)}
              <sup>o</sup>C / {Math.floor(data.main.temp - 273.15)}
              <sup>o</sup>C
            </p>
            <p className="weather-main">{data.weather[0].main}</p>
            <img src={iconurl} alt="" className="weather-icon" />
            <p>Sunrise: {data.sys.sunrise}</p>
            <p>Sunset: {data.sys.sunset}</p>
          </div>
          <div className="details">
            <table>
              <tr>
                <th>Humidity</th>
                <th>Pressure</th>
              </tr>
              <tr>
                <td>{data.main.humidity}</td>
                <td>{data.main.pressure}</td>
              </tr>
            </table>
          </div>
        </React.Fragment>
      ) : (
        <div className="main-card">
          <h2>{message}</h2>
        </div>
      )}
    </div>
  );
}

export default DisplayWeather;
